</style>

<div class=\"example-wrapper\">
    <h1>Découvrez nos magnifiques régions !</h1>
    <table class=\"table\">

    {% for region in regions %}

    <tr>
        <td>
            {{ region.name }}
        </td>
        <td>
            {{ region.presentation }}
        </td>
        <td><a href=\"{{ path('region_show', {'id': region.id}) }}\">show</a></td>
    </tr>
    {% else %}
        <tr>
            <td colspan=\"6\">no records found</td>
        </tr>
    {% endfor %}
    </table>
</div>

{% endblock %}
